import telebot
from telebot import custom_filters
from telebot import StateMemoryStorage
from telebot.handler_backends import StatesGroup, State


state_storage = StateMemoryStorage()
# –í—Å—Ç–∞–≤–∏—Ç—å —Å–≤–æ–π —Ç–æ–∫–µ—Ç –∏–ª–∏ –æ—Å—Ç–∞–≤–∏—Ç—å –∫–∞–∫ –µ—Å—Ç—å, —Ç–æ–≥–¥–∞ –º—ã —Å–æ–∑–¥–∞–¥–∏–º –µ–≥–æ —Å–∞–º–∏
bot = telebot.TeleBot("6650142890:AAG-Kpsw8sS3DlnipPIISAkWrH-LHdWaDhI",
                      state_storage=state_storage, parse_mode='Markdown')


class PollState(StatesGroup):
    name = State()
    age = State()


class HelpState(StatesGroup):
    wait_text = State()


text_poll = "—É–∑–Ω–∞–µ–º –¥—Ä—É–≥ –¥—Ä—É–≥–∞ –ø–æ–±–ª–∏–∂–µüòè"  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
text_button_1 = "—á—Ç–æ —Ç–∞–∫–æ–µ —è–∑—ã–∫ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—è?"  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
text_button_2 = "–∫–∞–∫ –Ω–∞—É—á–∏—Ç—å—Å—è –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—é –∑–∞ –∫–æ—Ä–æ—Ç–∫–∏–π —Å—Ä–æ–∫?"  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
text_button_3 = "–∫–∞–∫ –∑–∞—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å –Ω–∞ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–∏?"  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç


menu_keyboard = telebot.types.ReplyKeyboardMarkup(one_time_keyboard=True, resize_keyboard=True)
menu_keyboard.add(
    telebot.types.KeyboardButton(
        text_poll,
    )
)
menu_keyboard.add(
    telebot.types.KeyboardButton(
        text_button_1,
    )
)

menu_keyboard.add(
    telebot.types.KeyboardButton(
        text_button_2,
    ),
    telebot.types.KeyboardButton(
        text_button_3,
    )
)


@bot.message_handler(state="*", commands=['start'])
def start_ex(message):
    bot.send_message(
        message.chat.id,
        '–ü—Ä–∏–≤–µ—Ç, —è –ø–æ–º–æ–≥—É —Ç–µ–±–µ —É–∑–Ω–∞—Ç—å –±–æ–ª—å—à–µ –æ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–∏ –∏ —Ä–∞—Å—Å–∫–∞–∂—É –∫–∞–∫ –º–æ–∂–Ω–æ –∑–∞—Ä–∞–±–æ—Ç–∞—Ç—å –Ω–∞ —ç—Ç–æ–º:) –Ω–æ –¥–ª—è –Ω–∞—á–∞–ª–∞, —É–∑–Ω–∞–µ–º –¥—Ä—É–≥ –¥—Ä—É–≥–∞ –ø–æ–±–ª–∏–∂–µüòâ',  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
        reply_markup=menu_keyboard)

@bot.message_handler(func=lambda message: text_poll == message.text)
def first(message):
    bot.send_message(message.chat.id, '–∫–∞–∫ —Ç–µ–±—è –∑–æ–≤—É—Ç?')  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
    bot.set_state(message.from_user.id, PollState.name, message.chat.id)


@bot.message_handler(state=PollState.name)
def name(message):
    with bot.retrieve_data(message.from_user.id, message.chat.id) as data:
        data['name'] = message.text
    bot.send_message(message.chat.id, '—Å–∫–æ–ª—å–∫–æ —Ç–µ–±–µ –ª–µ—Ç?')  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
    bot.set_state(message.from_user.id, PollState.age, message.chat.id)


@bot.message_handler(state=PollState.age)
def age(message):
    with bot.retrieve_data(message.from_user.id, message.chat.id) as data:
        data['age'] = message.text
    bot.send_message(message.chat.id, '–°–ø–∞—Å–∏–±–æ –∑–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—é!', reply_markup=menu_keyboard)  # –ú–æ–∂–Ω–æ –º–µ–Ω—è—Ç—å —Ç–µ–∫—Å—Ç
    bot.delete_state(message.from_user.id, message.chat.id)


